import styles from './CreatePost.module.css'

import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useAuthValue } from '../../context/AuthContext'
import { useInsertDocument } from '../../hooks/useInsertDocument'


const CreatePost = () => {
  const [title, setTitle] = useState('')
  const [image, setImage] = useState('')
  const [body, setBody] = useState('')
  const [tags, setTags] = useState([])
  const [formError, setFormError] = useState('')
  const {insertDocument, response} = useInsertDocument('posts')
  const {user} = useAuthValue()

  const handleSubmit = (e) =>{
    e.preventDefault()

    setFormError('')

    //validate image URL
    try {
      new URL(image);
    } catch (error) {
      setFormError("A imagem precisa ser uma URL.");
    }

    //criar array de tags

    const tagsArray = tags.split(",").map(tag => tag.trim().toLowerCase())

    //checar todos os valores

    if(!title || !image || !tags || !body){
      setFormError("Por favor, preencha todos os campos")
    }

    if(formError) return;

    insertDocument({
      title,
      image,
      body,
      tags,
      uid : user.uid,
      createBy: user.displayName
    })
  }



  return (
    <div className={styles.create_post}>
      <h2>Criar post</h2>
      <p>Escreva sobre o que quiser e compartilhe seu conhecimento</p>
      <form onSubmit={handleSubmit}>
        <label>
          <span>Título:</span>
          <input type="text" name="title" required placeholder='Pense num bom título...' onChange={e => setTitle(e.target.value)}/>
        </label>
        <label>
          <span>URL da imagem:</span>
          <input type="text" name="image" required placeholder='Insira uma imagem' onChange={e => setImage(e.target.value)}/>
        </label>
        <label>
          <span>Conteúdo:</span>
          <textarea name="body" required placeholder='Insira conteúdo do post' onChange={e => setBody(e.target.value)}/>
        </label>
        <label>
          <span>Tags</span>
          <input type="text" name="tags" required placeholder='Insira as tags separadas por vírgula' onChange={e => setTags(e.target.value)} />
        </label>
        {!response.loading && <button className='btn'>Cadastrar</button>}
        {response.loading && (<button className='btn' disabled>Aguarde...</button>)}
        {(response.error || formError) && (
          <p className="error">{response.error || formError}</p>
        )}
      </form>
    </div>
  )
}

export default CreatePost
